// EditStockMovement.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, MenuItem, Select, InputLabel, FormControl, Container } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';

const EditStockMovement = () => {
  const { id } = useParams();
  const [product, setProduct] = useState('');
  const [type, setType] = useState('');
  const [quantity, setQuantity] = useState('');
  const [location, setLocation] = useState('');
  const [operator, setOperator] = useState('');
  const [datetime, setDatetime] = useState('');
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch list of products
    axios.get('http://localhost:8080/api/products')
      .then(response => setProducts(response.data))
      .catch(err => console.error(err));

    // Fetch the stock movement to edit
    axios.get(`http://localhost:8080/api/mvt/${id}`)
      .then(response => {
        const { product, type, quantity, location, operator, datetime } = response.data;
        setProduct(product);
        setType(type);
        setQuantity(quantity);
        setLocation(location);
        setOperator(operator);
        setDatetime(datetime);
      })
      .catch(err => console.error(err));
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedStockMovement = {
      product,
      type,
      quantity,
      location,
      operator,
      datetime,
    };

    // Update stock movement
    axios.put(`http://localhost:8080/api/mvt/${id}`, updatedStockMovement)
      .then(() => {
        alert('Stock movement updated successfully!');
        navigate('/stockmovements');
      })
      .catch(err => {
        console.error('Error updating stock movement:', err);
        alert('There was an error updating the stock movement');
      });
  };

  return (
    <Container>
      <h2>Edit Stock Movement</h2>
      <form onSubmit={handleSubmit}>
        <FormControl fullWidth margin="normal">
          <InputLabel>Product</InputLabel>
          <Select
            value={product}
            onChange={(e) => setProduct(e.target.value)}
            required
          >
            {products.map((product) => (
              <MenuItem key={product._id} value={product.name}>
                {product.name}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        <TextField
          label="Type"
          value={type}
          onChange={(e) => setType(e.target.value)}
          fullWidth
          margin="normal"
          required
        />

        <TextField
          label="Quantity"
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          fullWidth
          margin="normal"
          required
        />

        <FormControl fullWidth margin="normal">
          <InputLabel>Location</InputLabel>
          <Select
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          >
            <MenuItem value="armoire 01">Armoire 01</MenuItem>
            <MenuItem value="armoire 02">Armoire 02</MenuItem>
            <MenuItem value="armoire 03">Armoire 03</MenuItem>
          </Select>
        </FormControl>

        <TextField
          label="Operator"
          value={operator}
          onChange={(e) => setOperator(e.target.value)}
          fullWidth
          margin="normal"
          required
        />

        <TextField
          label="Datetime"
          type="datetime-local"
          value={datetime}
          onChange={(e) => setDatetime(e.target.value)}
          fullWidth
          margin="normal"
          required
        />

        <Button variant="contained" type="submit" color="primary">
          Update Stock Movement
        </Button>
      </form>
    </Container>
  );
};

export default EditStockMovement;
