// StockMovementDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { Link } from 'react-router-dom';

const StockMovementDashboard = () => {
  const [stockMovements, setStockMovements] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch the list of stock movements
    axios.get('http://localhost:8080/api/mvt')
      .then(response => setStockMovements(response.data))
      .catch(err => console.error(err));

    // Fetch the list of products
    axios.get('http://localhost:8080/api/products')
      .then(response => setProducts(response.data))
      .catch(err => console.error(err));
  }, []);

  // Handle delete stock movement
  const handleDeleteStockMovement = (movementId) => {
    if (window.confirm('Are you sure you want to delete this stock movement?')) {
      axios.delete(`http://localhost:8080/api/mvt/${movementId}`)
        .then(() => {
          setStockMovements(stockMovements.filter(movement => movement._id !== movementId));
        })
        .catch(err => {
          console.error('Error deleting stock movement:', err);
          alert('There was an error deleting the stock movement');
        });
    }
  };

  return (
    <div>
      <h2>Stock Movements</h2>
      <Button variant="contained" component={Link} to="/add-stockmovement" style={{ marginBottom: '20px' }}>
        Add Stock Movement
      </Button>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell>Type</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Location</TableCell>
              <TableCell>Operator</TableCell>
              <TableCell>Datetime</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {stockMovements.map((movement) => (
              <TableRow key={movement._id}>
                <TableCell>{movement.product}</TableCell>
                <TableCell>{movement.type}</TableCell>
                <TableCell>{movement.quantity}</TableCell>
                <TableCell>{movement.location}</TableCell>
                <TableCell>{movement.operator}</TableCell>
                <TableCell>{movement.datetime}</TableCell>
                <TableCell>
                  <Button
                    variant="outlined"
                    color="primary"
                    component={Link}
                    to={`/edit-stockmovement/${movement._id}`}
                  >
                    Edit
                  </Button>
                  <Button
                    variant="outlined"
                    color="error"
                    onClick={() => handleDeleteStockMovement(movement._id)}
                    style={{ marginLeft: '10px' }}
                  >
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default StockMovementDashboard;
