// ProductDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, TextField, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';

const ProductDashboard = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', category: '', quantity: 0 });
  const [editingProduct, setEditingProduct] = useState(null);

  // Fetch products from the backend
  useEffect(() => {
    axios.get('http://localhost:8080/api/products')
      .then(response => setProducts(response.data))
      .catch(err => console.error(err));
  }, []);

  // Create a new product
  const handleCreateProduct = () => {
    axios.post('http://localhost:8080/api/products', newProduct)
      .then(response => {
        setProducts([...products, response.data]);
        setNewProduct({ name: '', category: '', quantity: 0 });
      })
      .catch(err => console.error(err));
  };

  // Edit an existing product
  const handleEditProduct = (productId) => {
    const product = products.find(p => p._id === productId);
    setEditingProduct({ ...product });
  };

  // Update an existing product
  const handleUpdateProduct = () => {
    axios.put(`http://localhost:8080/api/products/${editingProduct._id}`, editingProduct)
      .then(response => {
        setProducts(products.map(p => (p._id === editingProduct._id ? response.data : p)));
        setEditingProduct(null); // Reset editing mode
      })
      .catch(err => console.error(err));
  };

  // Delete a product
  const handleDeleteProduct = (productId) => {
    axios.delete(`http://localhost:8080/api/products/${productId}`)
      .then(() => {
        setProducts(products.filter(p => p._id !== productId));
      })
      .catch(err => console.error(err));
  };

  return (
    <div>
      <h2>Product Management</h2>

      {/* Product Form (Create/Edit) */}
      <TextField
        label="Name"
        value={editingProduct ? editingProduct.name : newProduct.name}
        onChange={(e) => (editingProduct ? setEditingProduct({ ...editingProduct, name: e.target.value }) : setNewProduct({ ...newProduct, name: e.target.value }))}
        fullWidth
        margin="normal"
      />
      <TextField
        label="Category"
        value={editingProduct ? editingProduct.category : newProduct.category}
        onChange={(e) => (editingProduct ? setEditingProduct({ ...editingProduct, category: e.target.value }) : setNewProduct({ ...newProduct, category: e.target.value }))}
        fullWidth
        margin="normal"
      />
      <TextField
        label="Quantity"
        type="number"
        value={editingProduct ? editingProduct.quantity : newProduct.quantity}
        onChange={(e) => (editingProduct ? setEditingProduct({ ...editingProduct, quantity: e.target.value }) : setNewProduct({ ...newProduct, quantity: e.target.value }))}
        fullWidth
        margin="normal"
      />

      {/* If editing, show "Update" button; if creating, show "Create" button */}
      <Button 
        variant="contained" 
        color="primary" 
        onClick={editingProduct ? handleUpdateProduct : handleCreateProduct}
      >
        {editingProduct ? 'Update Product' : 'Create Product'}
      </Button>

      <h3>Existing Products</h3>

      {/* Table displaying products */}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Category</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products.map((product) => (
              <TableRow key={product._id}>
                <TableCell>{product.name}</TableCell>
                <TableCell>{product.category}</TableCell>
                <TableCell>{product.quantity}</TableCell>
                <TableCell>
                  <Button variant="outlined" onClick={() => handleEditProduct(product._id)}>
                    Edit
                  </Button>
                  <Button variant="outlined" color="error" onClick={() => handleDeleteProduct(product._id)}>
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default ProductDashboard;
