// StockMovementList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography } from '@mui/material';
import { Link } from 'react-router-dom';

const StockMovementList = () => {
  const [stockMovements, setStockMovements] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:/api/mvt')
      .then(response => setStockMovements(response.data))
      .catch(error => console.error(error));
  }, []);

  const handleDelete = (movementId) => {
    if (window.confirm('Are you sure you want to delete this stock movement?')) {
      axios.delete(`http://localhost:/api/mvt/${movementId}`)
        .then(() => {
          setStockMovements(stockMovements.filter(movement => movement._id !== movementId));
        })
        .catch(error => console.error('Error deleting stock movement:', error));
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Stock Movements</Typography>
      <Button variant="contained" color="primary" component={Link} to="/add-stockmovement" style={{ marginBottom: '20px' }}>
        Add Stock Movement
      </Button>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell>Type</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Location</TableCell>
              <TableCell>Operator</TableCell>
              <TableCell>Datetime</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {stockMovements.map((movement) => (
              <TableRow key={movement._id}>
                <TableCell>{movement.product}</TableCell>
                <TableCell>{movement.type}</TableCell>
                <TableCell>{movement.quantity}</TableCell>
                <TableCell>{movement.location}</TableCell>
                <TableCell>{movement.operator}</TableCell>
                <TableCell>{movement.datetime}</TableCell>
                <TableCell>
                  <Button
                    variant="outlined"
                    color="primary"
                    component={Link}
                    to={`/edit-stockmovement/${movement._id}`}
                    style={{ marginRight: '10px' }}
                  >
                    Update
                  </Button>
                  <Button
                    variant="outlined"
                    color="error"
                    onClick={() => handleDelete(movement._id)}
                  >
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default StockMovementList;
