// ProductDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, TextField, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';

const ProductDashboard = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', description: '', quantity: 0 });
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8080/api/products')
      .then(response => setProducts(response.data))
      .catch(err => console.error(err));
  }, []);

  const handleCreateProduct = () => {
    axios.post('http://localhost:8080/api/products', newProduct)
      .then(response => {
        setProducts([...products, response.data]);
        setNewProduct({ name: '', description: '', quantity: 0 });
      })
      .catch(err => console.error(err));
  };

  const handleUpdateProduct = (productId) => {
    axios.put(`http://localhost:8080/api/products/${productId}`, editingProduct)
      .then(response => {
        setProducts(products.map(p => (p._id === productId ? response.data : p)));
        setEditingProduct(null);
      })
      .catch(err => console.error(err));
  };

  const handleDeleteProduct = (productId) => {
    axios.delete(`http://localhost:8080/api/products/${productId}`)
      .then(() => {
        setProducts(products.filter(p => p._id !== productId));
      })
      .catch(err => console.error(err));
  };

  return (
    <div>
      <h2>Product Management</h2>

      <TextField
        label="Name"
        value={newProduct.name}
        onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        fullWidth
        margin="normal"
      />
      <TextField
        label="Description"
        value={newProduct.description}
        onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
        fullWidth
        margin="normal"
      />
      <TextField
        label="Quantity"
        type="number"
        value={newProduct.quantity}
        onChange={(e) => setNewProduct({ ...newProduct, quantity: e.target.value })}
        fullWidth
        margin="normal"
      />

      <Button variant="contained" color="primary" onClick={handleCreateProduct}>
        Create Product
      </Button>

      <h3>Existing Products</h3>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Description</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products.map((product) => (
              <TableRow key={product._id}>
                <TableCell>{product.name}</TableCell>
                <TableCell>{product.description}</TableCell>
                <TableCell>{product.quantity}</TableCell>
                <TableCell>
                  <Button variant="outlined" onClick={() => setEditingProduct(product)}>
                    Edit
                  </Button>
                  <Button variant="outlined" color="error" onClick={() => handleDeleteProduct(product._id)}>
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default ProductDashboard;
