import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StockMovementDashboard = () => {
  const [stockMovements, setStockMovements] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:8080/api/mvt/')
      .then((response) => setStockMovements(response.data))
      .catch((error) => console.error('Error fetching stock movements:', error));
  }, []);

  return (
    <div>
      <h2>Stock Movement Dashboard</h2>
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Type</th>
            <th>Quantity</th>
            <th>Location</th>
            <th>Operator</th>
            <th>Datetime</th>
          </tr>
        </thead>
        <tbody>
          {stockMovements.map((movement) => (
            <tr key={movement._id}>
              <td>{movement.product.name}</td>
              <td>{movement.type}</td>
              <td>{movement.quantity}</td>
              <td>{movement.location}</td>
              <td>{movement.operator}</td>
              <td>{new Date(movement.datetime).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockMovementDashboard;
