// AddStockMovement.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, MenuItem, Select, InputLabel, FormControl, FormHelperText, Container } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const AddStockMovement = () => {
  const [products, setProducts] = useState([]);
  const [type, setType] = useState('');
  const [quantity, setQuantity] = useState('');
  const [location, setLocation] = useState('');
  const [operator, setOperator] = useState('');
  const [datetime, setDatetime] = useState('');
  const [product, setProduct] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch list of products to display in the dropdown
    axios.get('http://localhost:8080/api/products')
      .then(response => setProducts(response.data))
      .catch(err => console.error(err));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const stockMovement = {
      product,
      type,
      quantity,
      location,
      operator,
      datetime,
    };

    // Add stock movement
    axios.post('http://localhost:8080/api/mvt', stockMovement)
      .then(() => {
        alert('Stock movement added successfully!');
        navigate('/stockmovements'); // Redirect to stock movement dashboard
      })
      .catch(err => {
        console.error('Error adding stock movement:', err);
        alert('There was an error adding the stock movement');
      });
  };

  return (
    <Container>
      <h2>Add Stock Movement</h2>
      <form onSubmit={handleSubmit}>
        <FormControl fullWidth margin="normal">
          <InputLabel>Product</InputLabel>
          <Select
            value={product}
            onChange={(e) => setProduct(e.target.value)}
            required
          >
            {products.map((product) => (
              <MenuItem key={product._id} value={product.name}>
                {product.name}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        <TextField
          label="Type"
          value={type}
          onChange={(e) => setType(e.target.value)}
          fullWidth
          margin="normal"
          required
        />

        <TextField
          label="Quantity"
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          fullWidth
          margin="normal"
          required
        />

        <FormControl fullWidth margin="normal">
          <InputLabel>Location</InputLabel>
          <Select
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          >
            <MenuItem value="armoire 01">Armoire 01</MenuItem>
            <MenuItem value="armoire 02">Armoire 02</MenuItem>
            <MenuItem value="armoire 03">Armoire 03</MenuItem>
          </Select>
        </FormControl>

        <TextField
          label="Operator"
          value={operator}
          onChange={(e) => setOperator(e.target.value)}
          fullWidth
          margin="normal"
          required
        />

        <TextField
          label="Datetime"
          type="datetime-local"
          value={datetime}
          onChange={(e) => setDatetime(e.target.value)}
          fullWidth
          margin="normal"
          required
        />

        <Button variant="contained" type="submit" color="primary">
          Add Stock Movement
        </Button>
      </form>
    </Container>
  );
};

export default AddStockMovement;
